              <h3 className="text-xl font-semibold mb-2">
                {showRegister ? "Compra y vende" : "Iniciar SesiÃ³n"}
              </h3>
              <p className="text-gray-600 text-sm">
                {showRegister 
                  ? "RegÃ­strate para publicar productos"
                  : "Inicia sesiÃ³n en tu cuenta"
                }
              </p>
            </div>

            {showRegister ? (
              <form onSubmit={handleRegister} className="space-y-4">
                <Input 
                  name="registrationCode" 
                  placeholder="CÃ³digo de registro (proporcionado por admin)" 
                  required 
                  data-testid="input-registration-code"
                  className="border-red-300 focus:border-red-500"
                />
                <Input 
                  name="name" 
                  placeholder="Nombre" 
                  required 
                  data-testid="input-name"
                />
                <Input 
                  name="email" 
                  type="email" 
                  placeholder="Email" 
                  required 
                  data-testid="input-email"
                />
                <Input 
                  name="location" 
                  placeholder="UbicaciÃ³n (Ciudad, PaÃ­s)" 
                  required 
                  data-testid="input-location"
                />
                <Button 
                  type="submit" 
                  className="w-full bg-green-600 hover:bg-green-700" 
                  disabled={registerMutation.isPending}
                  data-testid="button-register"
                >
                  {registerMutation.isPending ? "Registrando..." : "Registrarse"}
                </Button>
              </form>
            ) : (
              <form onSubmit={handleLogin} className="space-y-4">
                <Input 
                  name="email" 
                  type="email" 
                  placeholder="Email" 
                  required 
                  data-testid="input-login-email"
                />
                <Button 
                  type="submit" 
                  className="w-full bg-green-600 hover:bg-green-700" 
                  disabled={loginMutation.isPending}
                  data-testid="button-login"
                >
                  {loginMutation.isPending ? "Ingresando..." : "Iniciar sesiÃ³n"}
                </Button>
              </form>
            )}

            <p className="text-center mt-4 text-sm">
              {showRegister ? "Â¿Ya tienes cuenta?" : "Â¿No tienes cuenta?"}{" "}
              <button
                type="button"
                className="text-green-600 hover:underline"
                onClick={() => setShowRegister(!showRegister)}
                data-testid="button-toggle-auth"
              >
                {showRegister ? "Iniciar sesiÃ³n" : "Registrarse"}
              </button>
            </p>
          </div>
        </div>
      </div>
    );
  }

  // Product Detail Modal
  if (selectedProduct) {
    return (
      <div className="h-full flex flex-col bg-white">
        <div className="bg-white border-b border-gray-200 flex items-center justify-between p-4">
